export const OrderHistory: React.FC<Props> = ({orders}) => {
  const [openAccordions, setOpenAccordions] = useState<Set<string>>(new Set());

  useEffect(() => {
    document.body.style.backgroundColor = '#F5FAFB';
  }, []);

  const handleToggle = (id: number) => {
    setOpenAccordions((prev) => {
      const newSet = new Set(prev);
      const idStr = id.toString();
      if (newSet.has(idStr)) {
        newSet.delete(idStr);
      } else {
        newSet.add(idStr);
      }
      return newSet;
    });
  };

  const renderHeader = () => {
    return (
      <components.Header
        showGoBack={true}
        title='Order history'
      />
    );
  };

  const renderContent = () => {
    return (
      <main className='scrollable container'>
        <section
          className='accordion'
          style={{paddingTop: 20, paddingBottom: 20}}
        >
          {orders.map((order, index, array) => {
            const isLast = index === array.length - 1;

            const idStr = order.id.toString();
            const isOpen = openAccordions.has(idStr);
            return (
              <div
                key={order.id}
                style={{
                  border: '1px solid #C8CDD9',
                  marginBottom: isLast ? 0 : 4,
                  borderRadius: 20,
                  backgroundColor: 'var(--white-color)',
                }}
              >
                <details
                  open={isOpen}
                  style={{borderRadius: 10}}
                  onToggle={() => handleToggle(order.id)}
                >
                  <summary style={{padding: 20}}>
                    <section
                      style={{
                        marginBottom: 5,
                        display: 'flex',
                        flexDirection: 'row',
                        alignItems: 'center',
                        justifyContent: 'space-between',
                      }}
                    >
                      <h5
                        style={{
                          marginRight: 4,
                          fontWeight: 500,
                          color: 'var(--main-color)',
                        }}
                      >
                        #{order.id}
                      </h5>

                      {order.status === 'shipping' && <OnItsWaySvg />}
                      {order.status === 'delivered' && <DeliveredSvg />}
                      {order.status === 'canceled' && <CanceledSvg />}
                    </section>
                    <section
                      style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        alignItems: 'center',
                      }}
                    >
                      <span
                        className='t12'
                        style={{fontFamily: 'var(--font-league-spartan)'}}
                      >
                        {order.date}
                      </span>
                      <span
                        className='t14'
                        style={{
                          fontWeight: 700,
                          color: 'var(--main-color)',
                          fontFamily: 'var(--font-league-spartan)',
                        }}
                      >
                        ${order.total}
                      </span>
                    </section>
                  </summary>
                  {/* Order details */}
                  <section style={{padding: 20}}>
                    <ul>
                      {order.products.map((product) => {
                        return (
                          <li
                            key={product.id}
                            style={{
                              marginBottom: 3,
                              display: 'flex',
                              justifyContent: 'space-between',
                              alignItems: 'center',
                            }}
                          >
                            <span className='t16'>{product.name}</span>
                            <span
                              style={{marginLeft: 'auto'}}
                              className='t16'
                            >
                              {product.quantity} x ${product.price}
                            </span>
                          </li>
                        );
                      })}
                      <li
                        style={{
                          display: 'flex',
                          flexDirection: 'row',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                        }}
                      >
                        <span className='t16'>Discount</span>
                        <span className='t16'>- ${order.discount}</span>
                      </li>
                    </ul>
                  </section>
                </details>
              </div>
            );
          })}
        </section>
      </main>
    );
  };

  return (
    <components.Screen>
      {renderHeader()}
      {renderContent()}
    </components.Screen>
  );
};
